import { createRouter, createWebHistory } from 'vue-router'
import HomeView from '../views/HomeView.vue'
import Login from '../views/LoginView.vue'
import Users from '../views/UsersView.vue'
import Services from '../views/ServicesView.vue'
import Categories from '../views/ProdutoCategoriaView.vue'
import Products from '../views/ProdutosView.vue'
import Orders from '../views/AgendamentosView.vue'
import Clients from '../views/ClientView.vue'
import AgendaHome from '../views/AgendaHomeVue.vue'
import axios from 'axios';

function AdminAuth(to,from, next){
  if(localStorage.getItem('token') != undefined){
    var req = {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("token")
      }
    }
    axios.post("http://localhost:8686/api/validate",{},req).then(res =>{
      console.log(res);
      next()
    }).catch(err =>{
      console.log(err)
      next("/login")
    })
  }else{
    next("/login");
  }
}
//ROTAS LIVRES\\
const routes = [
  {
    path: '/',
    name: 'home',
    component: HomeView
  },
  {
    path: '/agendamento',
    name: 'agendahome',
    component: AgendaHome,
    beforeEnter: AdminAuth
  },
   {
   path: '/login',
   name: 'login',
   component: Login
  },

  //ROTAS PROTEGIDAS\\
  {
    path:'/user',
    name:'user',
    component:  Users,
    beforeEnter: AdminAuth
  },
  {
  path:'/service',
  name:'service',
  component:  Services,
  beforeEnter: AdminAuth
},
{
  path:'/categories',
  name:'category',
  component:  Categories,
  beforeEnter: AdminAuth
},
{
  path:'/products',
  name:'product',
  component:  Products,
  beforeEnter: AdminAuth
},
{
  path:'/serviceOrder',
  name:'serviceOrder',
  component:  Orders,
  beforeEnter: AdminAuth
},
{
  path:'/client',
  name:'client',
  component:  Clients,
  beforeEnter: AdminAuth
},

  {
    path: '/admin/users',
    name: 'admin',
    component: HomeView,
    beforeEnter: AdminAuth
   },

]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

export default router
